<script>
  import { mapState, mapActions, mapMutations, mapGetters } from 'vuex';
  import Bus from '../Bus';

  export default {
    data () {
      return {
        selected: 0,
        lastWheel: 0
      }
    },
    computed: {
      ...mapState('Header/Appearance', ['promoOverlayVisible'])
    },
    methods: {
      wheel (e) {
        const lastWheel = this.lastWheel;
        this.lastWheel = e.timeStamp;
        
        if (e.timeStamp - lastWheel < 200) {
          return;
        }
        
        const selected = Math.min(2, Math.max(0,this.selected + Math.sign(e.deltaY)));
        
        if (selected !== this.selected) {
          this.selected = selected;
        }
      },
      ...mapMutations('Header/Appearance', ['togglePromoOverlay']),
      marginTop (i) {
        if (i === this.selected) return 0;
        if (i < this.selected) return '-125%';
        return '125%';
      },
      shift () {
        this.selected++;
      }
    },
    created() {
      Bus.$on('keydown', e => {
        if (e.which === 40) this.selected = Math.min(this.selected + 1, 2);
        if (e.which === 38) this.selected = Math.max(this.selected - 1, 0);
      });
    }
  }
</script>